const Errors = require('./errors.js')
module.exports = (name, cb, client) => {
  if (typeof name !== "string") {
    cb(new Errors.InvalidDataTypeError("configuration must be an string"), null)
  } else {
    let sql = `DROP TABLE ${name};`
    require('./query.js')(sql, cb, undefined, client, { noSQL: true })
  }
}
