const MariaDB = require('./maria.js')
const client = new MariaDB.Client({ database: "tachi", password: "tachi" })
client.on("error", (error) => {
  console.log(error)
})
client.query(`SELECT * from guilds`, (err, data) => {
  console.log(data)
  if (err) {
    throw err
  }
})
let columnArray = ["personID", "LastName", "FirstName", "Address", "City"]
client.createTable({
  name: "members",
  columns: client.columnGenerator(columnArray),
}, (err, data) => {
  console.log(data)
  if (err) {
    throw err
  } else {
    client.dropTable("members", (err, data) => {
      if (err) {
        throw err
      } else {
        console.log("Tests Complete")
        process.exit()
      }
    })
  }
})
